package com.seygen;

public class DoWhileLoopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

			int i=1;  // Declaring and Initializing the value to i
			do {
				System.out.println("The value of i:"+i);  // displaying the msg to user
				i++; // incrementing the value of i 1 upto 10
				
			} while (i<=10); // checking the condition the of i
	}

}
